import React,{Fragment,useEffect} from 'react';
import Producto from './Producto';
import {useDispatch,useSelector} from 'react-redux';
import {obtenerProductosAction} from '../actions/productionActions';

const Productos = () => {



    const dispatch = useDispatch();

    useEffect( ()=> {

        // Consultar la api
        const cargarProductos = () => dispatch( obtenerProductosAction() );
        cargarProductos();
        // eslint-disable-next-line
    }, []);

    // obtener el state
    const productos = useSelector( state => state.articulos.productos );
    const error = useSelector(state => state.articulos.error);
    const cargando = useSelector(state => state.articulos.loading);
     console.log(productos);
   
   return (  <Fragment>
        <h2 className="text-center my-5">Listado de Productos</h2>

        { error ? <p className="font-weight-bold alert alert-danger text-center mt-4">Hubo un error</p> : null }
        
        { cargando ? <p className="text-center">Cargando....</p> : null }

        <table className="table table-striped">
            <thead className="bg-primary table-dark">
                 <tr>
                     <th scope="col">Nombre</th>
                     <th scope="col">Precio</th>
                     <th scope="col">Acciones</th>
                 </tr>
            </thead>
            <tbody>
                { productos.length === 0 ? 'No hay productos' : (
                    productos.map(producto => (
                        <Producto
                             key={producto.id}
                             producto={producto}
                        />
                    ))
                ) }
            </tbody>
        </table>
    </Fragment>
     );
}
 
export default Productos;
